import * as React from "react";
import {Container, Paper} from "@mui/material";
import Box from "@mui/material/Box";
import {useEffect} from "react";
import {ObjectMap as ymaps} from "@sinclair/typebox";

export default function PlaceInformation() {

    const mapContainer = useRef(null);

    useEffect(() => {
        const loadMap = () => {
            if (!window.ymaps) return; // Проверяем доступность ymaps
            const myMap = new window.ymaps.Map(mapContainer.current, {
                center: [55.76, 37.64],
                zoom: 7
            });
        };

        // Загружаем API Яндекс.Карт и после загрузки создаем карту
        const script = document.createElement("script");
        script.src = "https://api-maps.yandex.ru/2.1/?apikey=ваш API-ключ&lang=ru_RU";
        script.async = true;
        script.onload = loadMap;
        document.body.appendChild(script);

        // Очищаем загруженный скрипт после удаления компонента
        return () => {
            document.body.removeChild(script);
        };
    }, []);

    const paperStyle = {
        padding: "50px 20px",
        width: "95%",
        margin: "20px auto",
        borderRadius: 20,
        backgroundColor: "#e9dbd3"
    }


    return (
        <Container>
            <Paper elevation={3} style={paperStyle}>
                <Box>
                    <h1>Место Проведения</h1>

                    <h3 className="textContent" style={{fontSize: "2rem"}}>
                        "Villa Небеса"
                        <tr/>
                        Ул. Техническая, 4б
                    </h3>

                    <div id="map" style={{width: "600px", height: "400px"}}>
                        1
                    </div>

                </Box>
            </Paper>
        </Container>
    );
}
